package Lex_Introduction_To_Java;

public class a_003_Simple_interest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p = 10000;
		float r = 3.5f;
		float t = 2.0f;
		float si = (p*r*t)/100;
		System.out.println("Simple Interest Calculated : " + (int)si);

	}

}
