package day2;

public class maximum_three_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =16 , b =10 , c = 26;
        
		if(a>b && a>c)
			System.out.println(a + " is biggest");
		if(b>a && b>c)
			System.out.println(b + " is biggest");
		else
			System.out.println(c + " is biggest");
		
	}

}
