package day2;

public class if_else_demo_exercise1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 6;
		byte b = 5;
		if(a==b)
		   System.out.println(a+b);
		else
		   System.out.println(2*(a+b));

	}

}

